import React, { useState } from 'react';
import './App.css';

function App() {
  const [change, setChange] = useState(false)
  function handleSubmit() {
    console.log("here")
    return (
      <div>
        <span>Value is sent</span>
      </div>
    )
  }
  return (
    <div className="App">
      <header className="App-header">
        Sarcasm Detector
      <div>
          <form>
            <input className="" name="text" type="text" placeholder="Enter text here" />
            <button type="submit" className="" onClick={() => setChange(!change)} onSubmit={handleSubmit}>Save</button>
          </form>
          {change && <h1>value sent!</h1>}
        </div>
      </header>
    </div>
  );
}

export default App;
