import React, { useState } from 'react';
import Form from "./form"
import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import './App.css';

function App() {
  const [isLoading, setIsloading] = useState(false)
  const [isClicked, setIsClicked] = useState(false)

  function handleClick() {
    setIsClicked(!isClicked)
  }

  return (
    <div className="App">
      <header className="App-header">
        {isClicked ? <Form isClicked={isClicked} onClick={handleClick} /> :
          <div className="form-box p-4">
            <span className="heading">
              Sarcasm Detection using NLP
          </span>
            <hr style={{ backgroundColor: '#dadada' }} />
            <p style={{ color: "gray" }}>
              <h5>
                With the increased use of social media platforms by people across the world,<br />
                many new interesting NLP problems have come into existence. One such being <br />
                the detection of sarcasm in the social media texts. This project is built<br />
                using Pandas, Sklearn, TFIDF vectorizer and logistic regression with<br />
                the accuracy of 73%.<br />
                <div className="mt-4">
                  <span style={{ fontWeight: 800 }}>A Project by Riya Negi, Neel Gandhi, Stemy Tomy.</span>
                </div>
              </h5>
            </p>
            <button id="check_project" onClick={handleClick} className="button-style mt-2 p-2">Check Project</button>
          </div>}
      </header>
    </div>
  );
}

export default App;
