import React, { useState } from 'react';
import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import './App.css';

function App() {
  const [change, setChange] = useState(false)
  const [parent, setParent] = useState("")
  const [child, setChild] = useState("")

  const handleSubmit = event => {
    event.preventDefault();
    console.log("here :", parent, child)
    return (
      <div>
        <span>Value is sent</span>
      </div>
    )
  }
  return (
    <div className="App">
      <header className="App-header">
        <div className="form-box p-4">
          {/* <span className="heading">
            Sarcasm Detector
          </span>
          <hr style={{ backgroundColor: 'white' }} /> */}
          <div>
            <form onSubmit={handleSubmit}>
              <div className="d-flex flex-column">
                <input className="mt-3 input-sm" name="Parent" type="text" placeholder="Enter Parent text here"
                  value={parent} onChange={event => setParent(event.target.value)} />
                <input className="mt-3 input-sm" name="Child" type="text" placeholder="Enter Child text here"
                  value={child} onChange={event => setChild(event.target.value)} />
                <button type="submit" className="mt-3">Check</button>
              </div>
            </form>
            {change && <h1>value sent!</h1>}
          </div>
        </div>
      </header>
    </div>
  );
}

export default App;
