import React, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './App.css';


function FormInput() {
    const [change, setChange] = useState(true)
    const [parent, setParent] = useState("")
    const [child, setChild] = useState("")
    const [subr, setSubr] = useState("")
    const [isLoading, setIsloading] = useState(false)

    const notify = () =>
        toast.error('⚠️ All fields are required', {
            position: "top-right",
            autoClose: 3000,
            hideProgressBar: true,
            closeOnClick: true,
            pauseOnHover: true,
            draggable: false,
            progress: undefined,
        });

    const handleSubmit = event => {
        event.preventDefault();
        // console.log("here :", parent, child, subr)
        // return (
        //   <div>
        //     <span>Value is sent</span>
        //   </div>
        // )

        setChange(!change)

        fetch('http://127.0.0.1:5000/detection/',
            {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                method: 'POST',
                body: parent, child, subr
            })
            .then(response => response.json())
            .then(response => {
                this.setState({
                    result: response.result,
                    isLoading: false
                });
            });
    }
    return (
        (
            <div className="form-box p-4">
                <span className="heading">
                    Sarcasm Detector
          </span>
                <hr style={{ backgroundColor: '#dadada' }} />
                {change ? <div>
                    <form onSubmit={handleSubmit}>
                        <div className="d-flex flex-column">
                            <input className="input-style input-sm mt-3" name="Parent" type="text" placeholder="Enter Parent text here"
                                value={parent} onChange={event => setParent(event.target.value)} />
                            <input className="input-style mt-5 input-sm" name="Child" type="text" placeholder="Enter Child text here"
                                value={child} onChange={event => setChild(event.target.value)} />
                            <input className="input-style input-sm mt-5" name="Subr" type="text" placeholder="Enter Subreddit's name here"
                                value={subr} onChange={event => setSubr(event.target.value)} />
                            <button type="submit" onClick={() => handleSubmit()} className="button-style mt-5 p-2">Check</button>
                        </div>
                    </form>
                </div> :
                    <div className="d-flex flex-column">
                        {parent.length % 2 === 0 ? <span className="answer"> This comment is Sarcastic.</span>
                            : <span className="answer"> This comment is not Sarcastic.</span>
                        }
                        <button onClick={() => setChange(!change)} className="button-style mt-5 p-2">Recheck</button>
                        {/* {isLoading ?
          <div className="d-flex flex-column">
            <Loader type="BallTriangle" color="white" height={80} width={80} />
            <span className="mt-5"> <h4>Please wait while we detect sarcasm</h4></span>
          </div> : <Form />} */}
                    </div>
                }
            </div>
        )

    )
}

export default FormInput
