import React from "react"
import './App.css';

const handleSubmit = event => {
    event.preventDefault();
    // console.log("here :", parent, child, subr)
    // return (
    //   <div>
    //     <span>Value is sent</span>
    //   </div>
    // )
    fetch('http://127.0.0.1:5000/detection/',
        {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            method: 'POST',
            body: parent, child, subr
        })
        .then(response => response.json())
        .then(response => {
            this.setState({
                result: response.result,
                isLoading: false
            });
        });
}

function form() {
    return (
        <form onSubmit={handleSubmit}>
            <div className="d-flex flex-column">
                <input className="input-style input-sm mt-3" name="Parent" type="text" placeholder="Enter Parent text here"
                    value={parent} onChange={event => setParent(event.target.value)} />
                <input className="input-style mt-5 input-sm" name="Child" type="text" placeholder="Enter Child text here"
                    value={child} onChange={event => setChild(event.target.value)} />
                <input className="input-style input-sm mt-5" name="Subr" type="text" placeholder="Enter Subreddit's name here"
                    value={subr} onChange={event => setSubr(event.target.value)} />
                <button type="submit" disabled={(parent.length && child.length && subr.length) < 1} onClick={() => setChange(!change)} className="button-style mt-5 p-2">Check</button>
            </div>
        </form>
    )
}

export default form
