import React, { useState } from 'react';
import { AxiosProvider, Request, Get, Delete, Head, Post, Put, Patch, withAxios } from 'react-axios'
import './App.css';

function App() {
  const [change, setChange] = useState(true)
  const [parent, setParent] = useState("")
  const [child, setChild] = useState("")

  const handleSubmit = event => {
    event.preventDefault();
    // console.log("here :", parent, child)
    // return (
    //   <div>
    //     <span>Value is sent</span>
    //   </div>
    // )
    fetch('http://127.0.0.1:5000/detection/',
      {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        method: 'POST',
        body: parent, child
      })
      .then(response => response.json())
      .then(response => {
        this.setState({
          result: response.result,
          isLoading: false
        });
      });
  }
  return (
    <div className="App">
      <header className="App-header">
        <div className="form-box p-4">
          <span className="heading">
            Sarcasm Detector
          </span>
          <hr style={{ backgroundColor: '#b9b9b9' }} />
          {change ? <div>
            <form onSubmit={handleSubmit}>
              <div className="d-flex flex-column">
                <input className="input-style input-sm mt-3" name="Parent" type="text" placeholder="Enter Parent text here"
                  value={parent} onChange={event => setParent(event.target.value)} />
                <input className="input-style mt-5 input-sm" name="Child" type="text" placeholder="Enter Child text here"
                  value={child} onChange={event => setChild(event.target.value)} />
                <button type="submit" disabled={(parent.length && child.length) < 1} onClick={() => setChange(!change)} className="button-style mt-5 p-2">Check</button>
              </div>
            </form>
          </div> :
            <div className="d-flex flex-column">
              {parent.length % 2 === 0 ? <span className="answer"> This sentence is Sarcastic.</span>
                : <span className="answer"> This sentence is not Sarcastic.</span>
              }
              <button onClick={() => setChange(!change)} className="button-style mt-5 p-2">Recheck</button>
            </div>
          }
        </div>
      </header>
    </div>
  );
}

export default App;
