import './App.css';

function App() {
  const [change, setChange] = useState(false)
  const [parent, setParent] = useState("")
  const [child, setChild] = useState("")

  const handleSubmit = event => {
    event.preventDefault();
    console.log("here :", parent, child)
    return (
      <div>
        <span>Value is sent</span>
      </div>
    )
  }
  return (
    <div className="App">
      <header className="App-header">
        <h1>Sarcasm Detector</h1>
        <div>
          <form onSubmit={handleSubmit}>
            <div className="">
              <input className="" name="Parent" type="text" placeholder="Enter Parent text here"
                value={parent} onChange={event => setParent(event.target.value)} />
              <input className="" name="CHild" type="text" placeholder="Enter Child text here"
                value={child} onChange={event => setChild(event.target.value)} />
              <button type="submit" className="">Send</button>
            </div>
          </form>
          {change && <h1>value sent!</h1>}
        </div>
      </header>
    </div>
  );
}

export default App;
