import React from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import Tooltipp from 'react-bootstrap/Tooltip'
import OverlayTrigger from 'react-bootstrap/OverlayTrigger'
const data = [
    {
        name: '1st Jan 2021',
        uv: 4,
        pv: 2,
        amt: 2,
    },
    {
        name: '10th Jan 2021',
        uv: 2,
        pv: 1,
        amt: 2,
    },
    {
        name: '20th Jan 2021',
        uv: 9,
        pv: 9,
        amt: 2,
    },
    {
        name: '30th Jan 2021',
        uv: 1,
        pv: 3,
        amt: 2,
    },
    {
        name: '10th Feb 2021',
        uv: 1,
        pv: 4,
        amt: 2,
    },
    {
        name: '20th Feb 2021',
        uv: 1,
        pv: 3,
        amt: 2,
    },
    {
        name: '30th Feb 2021',
        uv: 3,
        pv: 4,
        amt: 2,
    },
    {
        name: '10th Mar 2021',
        uv: 1,
        pv: 4,
        amt: 4,
    },
    {
        name: '20th Mar 2021',
        uv: 2,
        pv: 4,
        amt: 2,
    },
    {
        name: '30th Mar 2021',
        uv: 1,
        pv: 4,
        amt: 2,
    },
    {
        name: '10th Apr 2021',
        uv: 3,
        pv: 1,
        amt: 3,
    },
    {
        name: '20th Apr 2021',
        uv: 2,
        pv: 9,
        amt: 2,
    },
    {
        name: '30th Apr 2021',
        uv: 1,
        pv: 3,
        amt: 5,
    },
];

const Predictor = () => {
    return <div style={{ width: 700, height: 260 }}>
        <OverlayTrigger
            placement={'right'}
            overlay={
                <Tooltipp style={{ opacity: 0.7 }} >
                    See how much outreach your brand is getting across various social media platforms
        </Tooltipp>
            }
        >
            <div className="card-title text-left">Outreach Predictor</div>
        </OverlayTrigger>
        < ResponsiveContainer width="100%" height="100%">
            <AreaChart
                width={200}
                height={150}
                data={data}
                margin={{
                    top: 10,
                    right: 30,
                    left: 0,
                    bottom: 0,
                }}
            >
                {/* <defs>
                    <linearGradient id="colorUv" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#E25960" stopOpacity={0.8} />
                        <stop offset="95%" stopColor="#E25960" stopOpacity={0} />
                    </linearGradient>
                    <linearGradient id="colorPv" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%" stopColor="#323232" stopOpacity={0.8} />
                        <stop offset="95%" stopColor="#323232" stopOpacity={0} />
                    </linearGradient>
                </defs> */}
                <XAxis dataKey="name" />
                {/* <CartesianGrid strokeDasharray="5 5" /> */}
                <YAxis />
                <Tooltip />
                <Area type="monotone" dataKey="uv" stroke="#627aa4" fill="#627aa4" />
                <Area type="monotone" dataKey="pv" stroke="#eb8c9c" fill="#eb8c9c" />
            </AreaChart>
        </ResponsiveContainer>
    </div>
}

export default Predictor
