import React, { useEffect, useContext } from 'react'
import { GlobalContext } from "../../../context/Provider"
import "./sentiment.css"

const Legend = () => {
    const { dashboardState } = useContext(GlobalContext);
    const data = dashboardState.sentimentData.pie

    const Colors = ["#8495F5", "#A7B4FF", "#C6CFFE", "#D5DBFD", "#5E77FF"];

    return (
        <div className="legend">
            {data.map((index, key) => (<div>
                {console.log("colour->", index, key)}
                <div className="legendColor" style={{ backgroundColor: Colors[index] }}></div>
                <div className="legendName">{index.name}</div></div>))}
        </div>
    )
}

export default Legend
