import React from 'react';

export default function BuzzBody({ data }) {
    const imgHandler = value => {
        switch (value) {
            case 'Reddit':
                return 'rimg';
            case 'Youtube':
                return 'yimg';
            case 'Twitter':
                return 'timg';
        }
    };
    return (
        <a className="panel-block All">
            <div className="flex-column">
                <div className="social-title ml-3 font-weight-bold">
                    {imgHandler(data.media)}
                    {data.media}
                </div>
                <div style={{ width: 520, marginLeft: 20 }}>{data.title}</div>
            </div>
        </a>
    );
}
