import React from 'react'
import { ResponsiveStream } from '@nivo/stream'

const data = [
    {
        "Raoul": 55,
        "Josiane": 113,
        "Marcel": 137,
        "René": 60,
        "Paul": 140,
        "Jacques": 37
    },
    {
        "Raoul": 110,
        "Josiane": 31,
        "Marcel": 78,
        "René": 39,
        "Paul": 131,
        "Jacques": 174
    },
    {
        "Raoul": 89,
        "Josiane": 137,
        "Marcel": 110,
        "René": 197,
        "Paul": 46,
        "Jacques": 21
    },
    {
        "Raoul": 53,
        "Josiane": 186,
        "Marcel": 138,
        "René": 112,
        "Paul": 17,
        "Jacques": 145
    },
    {
        "Raoul": 72,
        "Josiane": 150,
        "Marcel": 43,
        "René": 108,
        "Paul": 85,
        "Jacques": 100
    },
    {
        "Raoul": 134,
        "Josiane": 149,
        "Marcel": 28,
        "René": 93,
        "Paul": 127,
        "Jacques": 101
    },
    {
        "Raoul": 51,
        "Josiane": 62,
        "Marcel": 138,
        "René": 69,
        "Paul": 94,
        "Jacques": 150
    },
    {
        "Raoul": 16,
        "Josiane": 42,
        "Marcel": 71,
        "René": 129,
        "Paul": 112,
        "Jacques": 61
    },
    {
        "Raoul": 96,
        "Josiane": 96,
        "Marcel": 15,
        "René": 137,
        "Paul": 178,
        "Jacques": 180
    }
];



const Mention = ({ data /* see data tab */ }) => {
    return <div style={{ height: 300 }}>
        <ResponsiveStream
            data={data}
            keys={['Raoul', 'Josiane', 'Marcel', 'René', 'Paul', 'Jacques']}
            margin={{ top: 50, right: 110, bottom: 50, left: 60 }}
            axisTop={null}
            axisRight={null}
            axisBottom={{
                orient: 'bottom',
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: '',
                legendOffset: 36
            }}
            axisLeft={{ orient: 'left', tickSize: 5, tickPadding: 5, tickRotation: 0, legend: '', legendOffset: -40 }}
            offsetType="diverging"
            colors={{ scheme: 'nivo' }}
            fillOpacity={0.85}
            borderColor={{ theme: 'background' }}
            defs={[
                {
                    id: 'dots',
                    type: 'patternDots',
                    background: 'inherit',
                    color: '#2c998f',
                    size: 4,
                    padding: 2,
                    stagger: true
                },
                {
                    id: 'squares',
                    type: 'patternSquares',
                    background: 'inherit',
                    color: '#e4c912',
                    size: 6,
                    padding: 2,
                    stagger: true
                }
            ]}
            fill={[
                {
                    match: {
                        id: 'Paul'
                    },
                    id: 'dots'
                },
                {
                    match: {
                        id: 'Marcel'
                    },
                    id: 'squares'
                }
            ]}
            dotSize={8}
            dotColor={{ from: 'color' }}
            dotBorderWidth={2}
            dotBorderColor={{ from: 'color', modifiers: [['darker', 0.7]] }}
            legends={[
                {
                    anchor: 'bottom-right',
                    direction: 'column',
                    translateX: 100,
                    itemWidth: 80,
                    itemHeight: 20,
                    itemTextColor: '#999999',
                    symbolSize: 12,
                    symbolShape: 'circle',
                    effects: [
                        {
                            on: 'hover',
                            style: {
                                itemTextColor: '#000000'
                            }
                        }
                    ]
                }
            ]}
        />
    </div>

}

export default Mention
