import {
  OUTREACH_SUCCESS,
  ERROR,
  SENTIMENT_SUCCESS
} from "../../helpers/constants";
import { dashboardInitial } from "../InitialState"

const dashboard = (state, { payload, type }) => {
  switch (type) {
    case OUTREACH_SUCCESS:
      console.log("State check!!:", state)
      return {
        ...state,
        outreachData: payload.data
      }
    case SENTIMENT_SUCCESS:
      console.log("State check!!:", state)
      return {
        ...state,
        sentimentData: {
          line: payload,
          pie: payload
        }
      }
    case ERROR:
      return {
        ...state
      }
    default:
      return state;
  }
};

export default dashboard;
